-- Consulta que muestra las cuentas de ahorro con un interes mayor a 3 ordenadas por interes de forma descendente
SELECT *
FROM ObjBanco.cuentaAhorro
WHERE interes > 3
ORDER BY interes DESC;

-- Consulta que muestra las oficinas y cantidad de cuentas corrientes con un saldo mayor a 1000
SELECT Oficina, COUNT(*) as "Número de cuentas"
FROM ObjBanco.cuentaCorriente
WHERE Saldo > 1000
GROUP BY Oficina
ORDER BY Oficina DESC;

-- Consulta que muestra las operaciones ordenadas por importe de forma descendente limitando el resultado a 5
SELECT *
FROM ObjBanco.Operacion
ORDER BY Importe DESC
LIMIT 5;

